import { useState, useEffect } from "react";
import { useMutation, useQuery, useQueryClient } from "@tanstack/react-query";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { apiRequest } from "@/lib/queryClient";
import { toast } from "@/hooks/use-toast";
import { insertRequestSchema } from "@shared/schema";
import { z } from "zod";

const formSchema = insertRequestSchema.extend({
  groupId: z.string().min(1, "Please select a group"),
});

interface AddRequestModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  defaultGroupId?: string;
}

export function AddRequestModal({ open, onOpenChange, defaultGroupId }: AddRequestModalProps) {
  const queryClient = useQueryClient();

  // Get first workspace and board for demo
  const { data: workspaces } = useQuery({
    queryKey: ["/api/workspaces"],
  });

  const { data: boards } = useQuery({
    queryKey: ["/api/workspaces", workspaces?.[0]?.id, "boards"],
    enabled: !!workspaces?.[0]?.id,
  });

  const { data: groups } = useQuery({
    queryKey: ["/api/boards", boards?.[0]?.id, "groups"],
    enabled: !!boards?.[0]?.id,
  });

  const { data: users } = useQuery({
    queryKey: ["/api/users"],
  });

  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      creativeBrief: "",
      status: "working",
      priority: "medium",
      type: "",
      groupId: defaultGroupId || "",
      ownerId: null,
      assigneeId: null,
      prediction: "",
      position: "1",
    },
  });

  // Reset form when modal opens and set default group
  useEffect(() => {
    if (open) {
      form.reset({
        name: "",
        creativeBrief: "",
        status: "working",
        priority: "medium",
        type: "",
        groupId: defaultGroupId || "",
        ownerId: null,
        assigneeId: null,
        prediction: "",
        position: "1",
      });
    }
  }, [open, defaultGroupId, form]);

  const createRequestMutation = useMutation({
    mutationFn: async (data: z.infer<typeof formSchema>) => {
      const { groupId, ...requestData } = data;
      await apiRequest("POST", `/api/groups/${groupId}/requests`, requestData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/boards"] });
      toast({ title: "Request created successfully!" });
      form.reset();
      onOpenChange(false);
    },
    onError: () => {
      toast({ title: "Failed to create request", variant: "destructive" });
    },
  });

  const onSubmit = (data: z.infer<typeof formSchema>) => {
    createRequestMutation.mutate(data);
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="text-xl font-semibold text-monday-text">
            Add New Request
          </DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-monday-text font-medium">Request Name</FormLabel>
                  <FormControl>
                    <Input 
                      placeholder="Enter request name" 
                      {...field}
                      className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="creativeBrief"
              render={({ field }) => (
                <FormItem>
                  <FormLabel className="text-monday-text font-medium">Creative Brief</FormLabel>
                  <FormControl>
                    <Textarea 
                      placeholder="Describe your request..." 
                      rows={3}
                      {...field}
                      className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="priority"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-monday-text font-medium">Priority</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue">
                          <SelectValue placeholder="Select priority" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="low">Low</SelectItem>
                        <SelectItem value="medium">Medium</SelectItem>
                        <SelectItem value="high">High</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="type"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-monday-text font-medium">Type</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue">
                          <SelectValue placeholder="Select type" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        <SelectItem value="Social Media">Social Media</SelectItem>
                        <SelectItem value="Document">Document</SelectItem>
                        <SelectItem value="Landing Page">Landing Page</SelectItem>
                        <SelectItem value="Email Campaign">Email Campaign</SelectItem>
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <FormField
                control={form.control}
                name="groupId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-monday-text font-medium">Group</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value}>
                      <FormControl>
                        <SelectTrigger className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue">
                          <SelectValue placeholder="Select group" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {groups?.map((group) => (
                          <SelectItem key={group.id} value={group.id}>
                            {group.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />

              <FormField
                control={form.control}
                name="ownerId"
                render={({ field }) => (
                  <FormItem>
                    <FormLabel className="text-monday-text font-medium">Owner</FormLabel>
                    <Select onValueChange={field.onChange} defaultValue={field.value || ""}>
                      <FormControl>
                        <SelectTrigger className="focus:ring-2 focus:ring-monday-blue focus:border-monday-blue">
                          <SelectValue placeholder="Select owner" />
                        </SelectTrigger>
                      </FormControl>
                      <SelectContent>
                        {users?.map((user) => (
                          <SelectItem key={user.id} value={user.id}>
                            {user.name}
                          </SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                    <FormMessage />
                  </FormItem>
                )}
              />
            </div>

            <div className="flex items-center justify-end space-x-3 pt-4">
              <Button 
                type="button" 
                variant="outline"
                onClick={() => onOpenChange(false)}
                className="text-monday-text border-gray-300 hover:bg-gray-50"
              >
                Cancel
              </Button>
              <Button 
                type="submit" 
                className="bg-monday-blue hover:bg-blue-600 text-white"
                disabled={createRequestMutation.isPending}
              >
                {createRequestMutation.isPending ? "Creating..." : "Create Request"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/board-table.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { GroupSection } from "./group-section";
import { VibeTable } from "./vibe-table";
import { Button } from "@/components/ui/button";
import { Plus } from "lucide-react";

interface BoardTableProps {
  boardId: string;
}

export function BoardTable({ boardId }: BoardTableProps) {
  const { data: groups, isLoading } = useQuery({
    queryKey: ["/api/boards", boardId, "groups"],
  });

  const { data: requests } = useQuery({
    queryKey: ["/api/boards", boardId, "requests"],
  });

  const { data: users } = useQuery({
    queryKey: ["/api/users"],
  });

  if (isLoading) {
    return (
      <div className="p-6">
        <div className="space-y-6">
          {[1, 2, 3].map((i) => (
            <div key={i} className="bg-white rounded-lg shadow-monday p-4 animate-pulse">
              <div className="h-6 bg-gray-200 rounded w-1/3 mb-4"></div>
              <div className="space-y-3">
                <div className="h-4 bg-gray-200 rounded"></div>
                <div className="h-4 bg-gray-200 rounded w-2/3"></div>
              </div>
            </div>
          ))}
        </div>
      </div>
    );
  }

  // Group requests by group
  const requestsByGroup = requests?.reduce((acc, request) => {
    if (!acc[request.groupId]) {
      acc[request.groupId] = [];
    }
    acc[request.groupId].push(request);
    return acc;
  }, {} as Record<string, typeof requests>) || {};

  return (
    <div className="p-6">
      <div className="space-y-6">
        {groups?.map((group) => (
          <GroupSection
            key={group.id}
            group={group}
            requests={requestsByGroup[group.id] || []}
            users={users || []}
          />
        ))}

        {/* Add new group button */}
        <div className="mt-6">
          <Button 
            variant="outline" 
            className="w-full h-12 border-2 border-dashed border-gray-300 hover:border-monday-blue hover:bg-blue-50 transition-colors"
          >
            <Plus className="w-4 h-4 mr-2 text-monday-blue" />
            <span className="text-monday-blue font-medium">Add new group</span>
          </Button>
        </div>
      </div>
    </div>
  );
}

client/src/components/group-section.tsx.content:
import { useState } from "react";
import { useMutation, useQueryClient } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Collapsible, CollapsibleContent, CollapsibleTrigger } from "@/components/ui/collapsible";
import { InlineEdit } from "./inline-edit";
import { StatusBadge } from "./ui/status-badge";
import { PriorityBadge } from "./ui/priority-badge";
import { AddRequestModal } from "./add-request-modal";
import { apiRequest } from "@/lib/queryClient";
import { toast } from "@/hooks/use-toast";
import { ChevronDown, ChevronRight, Plus, MoreHorizontal } from "lucide-react";
import type { Group, Request, User } from "@shared/schema";

interface GroupSectionProps {
  group: Group;
  requests: Request[];
  users: User[];
}

export function GroupSection({ group, requests, users }: GroupSectionProps) {
  const [collapsed, setCollapsed] = useState(group.collapsed === "true");
  const [addRequestModalOpen, setAddRequestModalOpen] = useState(false);
  const queryClient = useQueryClient();

  const updateRequestMutation = useMutation({
    mutationFn: async ({ requestId, updates }: { requestId: string; updates: Partial<Request> }) => {
      await apiRequest("PATCH", `/api/requests/${requestId}`, updates);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/boards"] });
      toast({ title: "Request updated successfully" });
    },
    onError: () => {
      toast({ title: "Failed to update request", variant: "destructive" });
    },
  });

  const handleRequestUpdate = (requestId: string, field: string, value: string) => {
    updateRequestMutation.mutate({
      requestId,
      updates: { [field]: value }
    });
  };

  const getUserById = (id: string | null) => {
    if (!id) return null;
    return users.find(user => user.id === id);
  };

  const getUserInitials = (user: User | null) => {
    if (!user) return "?";
    return user.initials || user.name.split(" ").map(n => n[0]).join("").toUpperCase();
  };

  const getBorderColor = () => {
    switch (group.color) {
      case "#3498db": return "border-blue-400";
      case "#9b59b6": return "border-purple-400";
      case "#27ae60": return "border-green-400";
      default: return "border-gray-400";
    }
  };

  const getBackgroundColor = () => {
    switch (group.color) {
      case "#3498db": return "bg-blue-50";
      case "#9b59b6": return "bg-purple-50";
      case "#27ae60": return "bg-green-50";
      default: return "bg-gray-50";
    }
  };

  const getBadgeColor = () => {
    switch (group.color) {
      case "#3498db": return "bg-blue-100 text-blue-700";
      case "#9b59b6": return "bg-purple-100 text-purple-700";
      case "#27ae60": return "bg-green-100 text-green-700";
      default: return "bg-gray-100 text-gray-700";
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-monday overflow-hidden">
      <Collapsible open={!collapsed} onOpenChange={(open) => setCollapsed(!open)}>
        <div className={`p-4 flex items-center justify-between border-l-4 ${getBorderColor()} ${getBackgroundColor()}`}>
          <div className="flex items-center space-x-3">
            <CollapsibleTrigger asChild>
              <Button variant="ghost" size="sm" className="p-0 h-auto text-monday-text-muted hover:text-monday-text">
                {collapsed ? (
                  <ChevronRight className="w-4 h-4" />
                ) : (
                  <ChevronDown className="w-4 h-4" />
                )}
              </Button>
            </CollapsibleTrigger>
            <h3 className="text-lg font-semibold text-monday-text">{group.name}</h3>
            <Badge className={`text-xs font-medium ${getBadgeColor()}`}>
              {requests.length} {requests.length === 1 ? 'item' : 'items'}
            </Badge>
          </div>
          <Button 
            variant="ghost" 
            size="sm" 
            className="text-monday-text-muted hover:text-monday-blue"
            onClick={() => setAddRequestModalOpen(true)}
          >
            <Plus className="w-4 h-4" />
          </Button>
        </div>

        <CollapsibleContent>
          {requests.length > 0 ? (
            <>
              {/* Table Header */}
              <div className="grid grid-cols-12 gap-4 px-4 py-3 bg-gray-50 border-t border-gray-200 text-xs font-medium text-monday-text-secondary">
                <div className="col-span-2">Request</div>
                <div className="col-span-1">Owner</div>
                <div className="col-span-1">Priority</div>
                <div className="col-span-1">Status</div>
                <div className="col-span-1">Due Date</div>
                <div className="col-span-2">Creative Brief</div>
                <div className="col-span-1">Assignee</div>
                <div className="col-span-1">Type</div>
                <div className="col-span-2">Project Success Prediction</div>
              </div>

              {/* Table Rows */}
              {requests.map((request) => {
                const owner = getUserById(request.ownerId);
                const assignee = getUserById(request.assigneeId);

                return (
                  <div key={request.id} className="grid grid-cols-12 gap-4 px-4 py-4 border-t border-gray-100 items-center hover:bg-gray-50 transition-colors">
                    <div className="col-span-2">
                      <InlineEdit
                        value={request.name}
                        onSave={(value) => handleRequestUpdate(request.id, "name", value)}
                        className="p-2 rounded hover:bg-gray-100 transition-colors"
                      />
                    </div>

                    <div className="col-span-1">
                      {owner ? (
                        <div 
                          className="w-8 h-8 rounded-full flex items-center justify-center text-white text-xs font-medium"
                          style={{ backgroundColor: owner.color }}
                        >
                          {getUserInitials(owner)}
                        </div>
                      ) : (
                        <div className="w-8 h-8 bg-gray-200 rounded-full flex items-center justify-center">
                          <Plus className="w-3 h-3 text-gray-500" />
                        </div>
                      )}
                    </div>

                    <div className="col-span-1">
                      <PriorityBadge priority={request.priority as "low" | "medium" | "high"} />
                    </div>

                    <div className="col-span-1">
